// client/src/pages/Login/Login.jsx
import React, { useState } from 'react';
import './Login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

// Dans Login.jsx, modifiez la fonction handleSubmit
const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      
      const data = await response.json();
      
      if (data.success) {
        // Stockez les données utilisateur dans localStorage ou un état global
        localStorage.setItem('user', JSON.stringify(data.user));
        // Redirection vers le dashboard
        window.location.href = '/dashboard';
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError('Erreur de connexion au serveur');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <div id="login-header">
          <i className="fa-solid fa-boxes-stacked icon"></i>
          <h1>Stockify</h1>
        </div>

        {error && <div className="error-message">{error}</div>}
        
        <form id="login-form" onSubmit={handleSubmit}>
          <div className="input-group">
            <input 
              type="email" 
              placeholder="Adresse e-mail" 
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required 
            />
          </div>

          <div className="input-group">
            <input 
              type="password" 
              placeholder="Mot de passe" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required 
            />
          </div>

          <button type="submit" className="login-button">
            Se connecter
          </button>
        </form>

        <div className="login-footer">
          <p>
            Pas encore de compte ? 
            <span className="create-account">Créer un compte</span>
          </p>
          <span className="forgot-password">Mot de passe oublié ?</span>
        </div>
      </div>
      
      {/* Effet de souris lumineux */}
      <div id="mouse-glow"></div>
    </div>
  );
}

export default Login;
