// src/pages/Expenses/Expenses.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import "./Expenses.css";

function Expenses() {
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    id: null,
    name: "",
    category: "",
    amount: "",
    expense_date: "",
    notes: "",
  });
  const [isEditing, setIsEditing] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) {
      navigate("/");
      return;
    }
    fetchExpenses();
  }, [navigate]);

  const fetchExpenses = async () => {
    setLoading(true);
    try {
      const response = await fetch("http://localhost:5000/api/expenses");
      if (!response.ok) {
        throw new Error("Erreur lors de la récupération des dépenses");
      }
      const data = await response.json();
      setExpenses(data);
    } catch (err) {
      setError(err.message);
      console.error("Erreur:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.name || !formData.category || !formData.amount || !formData.expense_date) {
      alert("Veuillez remplir tous les champs obligatoires");
      return;
    }
    try {
      if (isEditing) {
        const response = await fetch(`http://localhost:5000/api/expenses/${formData.id}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(formData),
        });
        if (!response.ok) throw new Error("Erreur lors de la mise à jour");
        const updated = await response.json();
        setExpenses(expenses.map((e) => (e.id === updated.id ? updated : e)));
      } else {
        const response = await fetch("http://localhost:5000/api/expenses", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(formData),
        });
        if (!response.ok) throw new Error("Erreur lors de l'ajout");
        const newExpense = await response.json();
        setExpenses([...expenses, newExpense]);
      }
      resetForm();
    } catch (err) {
      console.error("Erreur:", err);
      alert("Une erreur est survenue");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const resetForm = () => {
    setFormData({ id: null, name: "", category: "", amount: "", expense_date: "", notes: "" });
    setIsEditing(false);
    setShowForm(false);
  };

  const handleEdit = (expense) => {
    setFormData({ ...expense });
    setIsEditing(true);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Supprimer cette dépense ?")) {
      try {
        const response = await fetch(`http://localhost:5000/api/expenses/${id}`, {
          method: "DELETE",
        });
        if (!response.ok) throw new Error();
        setExpenses(expenses.filter((e) => e.id !== id));
      } catch (err) {
        console.error("Erreur:", err);
        alert("Erreur lors de la suppression");
      }
    }
  };

  const formatDate = (date) => new Date(date).toLocaleDateString("fr-FR", { year: "numeric", month: "long", day: "numeric" });
  const formatAmount = (amt) => amt.toLocaleString("fr-FR", { style: "currency", currency: "EUR" });

  if (loading) return <div className="loading">Chargement...</div>;
  if (error) return <div className="error-message">Erreur: {error}</div>;

  return (
    <div className="expenses-page">
      <div className="expenses-header">
        <h1>Gestion des dépenses</h1>
        <button className="add-button" onClick={() => setShowForm(!showForm)}>
          {showForm ? "Annuler" : "Ajouter une dépense"}
        </button>
      </div>

      <AnimatePresence>
        {showForm && (
          <motion.div
            className="expense-form-container"
            initial={{ opacity: 0, height: 0, overflow: "hidden" }}
            animate={{ opacity: 1, height: "auto", overflow: "visible" }}
            exit={{ opacity: 0, height: 0, overflow: "hidden" }}
            transition={{ duration: 0.3, ease: "easeInOut" }}
          >
            <h2>{isEditing ? "Modifier la dépense" : "Nouvelle dépense"}</h2>
            <motion.form
              onSubmit={handleSubmit}
              className="expense-form"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.1, duration: 0.3 }}
            >
              {["name", "category", "amount", "expense_date", "notes"].map((field, index) => (
                <motion.div
                  className="form-group"
                  key={field}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.15 + index * 0.05, duration: 0.3 }}
                >
                  {field === "notes" ? (
                    <>
                      <label htmlFor="notes">Notes</label>
                      <textarea id="notes" name="notes" value={formData.notes} onChange={handleChange} rows="3" />
                    </>
                  ) : field === "category" ? (
                    <>
                      <label htmlFor="category">Catégorie</label>
                      <select id="category" name="category" value={formData.category} onChange={handleChange} required>
                        <option value="">Sélectionner une catégorie</option>
                        <option value="Locaux">Locaux</option>
                        <option value="Charges">Charges</option>
                        <option value="Services">Services</option>
                        <option value="Matériel">Matériel</option>
                        <option value="Salaires">Salaires</option>
                        <option value="Marketing">Marketing</option>
                        <option value="Autre">Autre</option>
                      </select>
                    </>
                  ) : (
                    <>
                      <label htmlFor={field}>
                        {field === "name" ? "Intitulé" : field === "amount" ? "Montant (€)" : "Date"}
                      </label>
                      <input
                        type={field === "amount" ? "number" : field === "expense_date" ? "date" : "text"}
                        id={field}
                        name={field}
                        value={formData[field]}
                        onChange={handleChange}
                        required={field !== "notes"}
                        step={field === "amount" ? "0.01" : undefined}
                        min={field === "amount" ? "0" : undefined}
                      />
                    </>
                  )}
                </motion.div>
              ))}

              <motion.div
                className="form-buttons"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.5, duration: 0.3 }}
              >
                <button type="button" onClick={resetForm} className="cancel-button">Annuler</button>
                <button type="submit" className="save-button">{isEditing ? "Mettre à jour" : "Enregistrer"}</button>
              </motion.div>
            </motion.form>
          </motion.div>
        )}
      </AnimatePresence>

      <div className="expenses-stats">
        {["Total", "Nombre", "Moyenne"].map((title, i) => (
          <motion.div
            key={title}
            className="stat-card"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.1 + i * 0.1, duration: 0.3 }}
          >
            <h3>{title === "Total" ? "Total des dépenses" : title === "Nombre" ? "Nombre de dépenses" : "Moyenne"}</h3>
            <p>
              {title === "Total"
                ? formatAmount(expenses.reduce((sum, e) => sum + e.amount, 0))
                : title === "Nombre"
                ? expenses.length
                : formatAmount(
                    expenses.length ? expenses.reduce((sum, e) => sum + e.amount, 0) / expenses.length : 0
                  )}
            </p>
          </motion.div>
        ))}
      </div>

      <motion.div
        className="expenses-table-container"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.4, duration: 0.5 }}
      >
        <table className="expenses-table">
          <thead>
            <tr>
              <th>Intitulé</th>
              <th>Catégorie</th>
              <th>Montant</th>
              <th>Date</th>
              <th>Notes</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {expenses.length === 0 ? (
              <tr>
                <td colSpan="6" className="no-data">Aucune dépense enregistrée</td>
              </tr>
            ) : (
              expenses.map((expense, index) => (
                <motion.tr
                  key={expense.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.5 + index * 0.05, duration: 0.3 }}
                >
                  <td>{expense.name}</td>
                  <td>{expense.category}</td>
                  <td className="amount-cell">{formatAmount(expense.amount)}</td>
                  <td>{formatDate(expense.expense_date)}</td>
                  <td className="notes-cell">{expense.notes}</td>
                  <td className="actions-cell">
                    <motion.button
                      className="edit-button"
                      onClick={() => handleEdit(expense)}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      Modifier
                    </motion.button>
                    <motion.button
                      className="delete-button"
                      onClick={() => handleDelete(expense.id)}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      Supprimer
                    </motion.button>
                  </td>
                </motion.tr>
              ))
            )}
          </tbody>
        </table>
      </motion.div>
    </div>
  );
}

export default Expenses;
