// src/pages/Dashboard/Dashboard.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

function Dashboard() {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Vérifier si l'utilisateur est connecté
    const user = JSON.parse(localStorage.getItem('user'));
    
    if (!user) {
      // Rediriger vers la page de connexion s'il n'y a pas d'utilisateur
      navigate('/');
      return;
    }
    
    setUserData(user);
    setLoading(false);
  }, [navigate]);

  if (loading) {
    return <div className="loading">Chargement...</div>;
  }

  return (
    <div className="dashboard-container">
      <h1>Tableau de bord</h1>
      
      <div className="dashboard-grid">
        <div className="dashboard-card">
          <h2>Inventaire</h2>
          <p>Nombre total d'articles : 124</p>
          <button className="action-btn">Voir l'inventaire</button>
        </div>

        <div className="dashboard-card">
          <h2>Ventes</h2>
          <p>Ventes du mois : 1,234 €</p>
          <button className="action-btn">Voir les ventes</button>
        </div>

        <div className="dashboard-card">
          <h2>Dépenses</h2>
          <p>Dépenses du mois : 567 €</p>
          <button className="action-btn">Voir les dépenses</button>
        </div>

        <div className="dashboard-card">
          <h2>Activités récentes</h2>
          <ul className="activity-list">
            <li>Nouveau stock ajouté - Il y a 2 heures</li>
            <li>Nouvelle vente enregistrée - Il y a 4 heures</li>
            <li>Mise à jour de l'inventaire - Il y a 5 heures</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
