import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';

import ConfirmationModal from '../../components/ConfirmationModal';
import Notification from '../../components/Notification';
import './Expenses.css';

function Expenses() {
  // State management
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // Form states
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    id: null,
    name: '',
    category: '',
    amount: '',
    expense_date: '',
    notes: '',
    recurring: false,
    recurring_period: ''
  });
  const [isEditing, setIsEditing] = useState(false);
  
  // Confirmation and Notification states
  const [confirmationModal, setConfirmationModal] = useState({
    isOpen: false,
    itemToDelete: null
  });
  const [notification, setNotification] = useState({
    show: false,
    message: '',
    type: 'success'
  });
  
  const navigate = useNavigate();

  // Fetch expenses on component mount
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    
    if (!user) {
      navigate('/');
      return;
    }
    
    fetchExpenses();
  }, [navigate]);

  // Fetch expenses from server
  const fetchExpenses = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/expenses');
      
      if (!response.ok) {
        throw new Error('Erreur lors de la récupération des dépenses');
      }
      
      const data = await response.json();
      setExpenses(data);
    } catch (err) {
      setError(err.message);
      console.error('Erreur:', err);
    } finally {
      setLoading(false);
    }
  };

  // Compute expense statistics
  const expenseStats = useMemo(() => {
    const total = expenses.reduce((sum, e) => sum + e.amount, 0);
    const average = expenses.length ? total / expenses.length : 0;
    
    return {
      total,
      count: expenses.length,
      average
    };
  }, [expenses]);

  // Handle form submission (add/edit)
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Basic validation
    if (!formData.name || !formData.category || !formData.amount || !formData.expense_date) {
      setNotification({
        show: true,
        message: 'Veuillez remplir tous les champs obligatoires',
        type: 'error'
      });
      return;
    }
    
    try {
      if (isEditing) {
        // Update existing expense
        const response = await fetch(`http://localhost:5000/api/expenses/${formData.id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });
        
        if (!response.ok) {
          throw new Error('Erreur lors de la mise à jour de la dépense');
        }
        
        const updatedExpense = await response.json();
        setExpenses(expenses.map(expense => 
          expense.id === updatedExpense.id ? updatedExpense : expense
        ));
        
        setNotification({
          show: true,
          message: 'Dépense mise à jour avec succès',
          type: 'success'
        });
      } else {
        // Add new expense
        const response = await fetch('http://localhost:5000/api/expenses', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });
        
        if (!response.ok) {
          throw new Error('Erreur lors de l\'ajout de la dépense');
        }
        
        const newExpense = await response.json();
        setExpenses([...expenses, newExpense]);
        
        setNotification({
          show: true,
          message: 'Dépense ajoutée avec succès',
          type: 'success'
        });
      }
      
      // Reset form
      resetForm();
    } catch (err) {
      console.error('Erreur lors de l\'enregistrement de la dépense:', err);
      setNotification({
        show: true,
        message: 'Une erreur est survenue lors de l\'enregistrement',
        type: 'error'
      });
    }
  };

  // Handle input changes in form
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  // Reset form to initial state
  const resetForm = () => {
    setFormData({
      id: null,
      name: '',
      category: '',
      amount: '',
      expense_date: '',
      notes: '',
      recurring: false,
      recurring_period: ''
    });
    setIsEditing(false);
    setShowForm(false);
  };

  // Prepare expense for editing
  const handleEdit = (expense) => {
    setFormData({
      id: expense.id,
      name: expense.name,
      category: expense.category,
      amount: expense.amount,
      expense_date: expense.expense_date,
      notes: expense.notes || '',
      recurring: expense.recurring || false,
      recurring_period: expense.recurring_period || ''
    });
    setIsEditing(true);
    setShowForm(true);
  };

  // Confirm deletion of an expense
  const handleDeleteConfirmation = (id) => {
    setConfirmationModal({
      isOpen: true,
      itemToDelete: id
    });
  };

  // Delete expense
  const handleDelete = async () => {
    const id = confirmationModal.itemToDelete;
    try {
      const response = await fetch(`http://localhost:5000/api/expenses/${id}`, {
        method: 'DELETE',
      });
      
      if (!response.ok) {
        throw new Error('Erreur lors de la suppression de la dépense');
      }
      
      setExpenses(expenses.filter(expense => expense.id !== id));
      
      // Close confirmation modal
      setConfirmationModal({ isOpen: false, itemToDelete: null });
      
      // Show success notification
      setNotification({
        show: true,
        message: 'Dépense supprimée avec succès',
        type: 'success'
      });
    } catch (err) {
      console.error('Erreur lors de la suppression:', err);
      
      // Show error notification
      setNotification({
        show: true,
        message: 'Une erreur est survenue lors de la suppression',
        type: 'error'
      });
    }
  };

  // Format date for display
  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString('fr-FR', {
      year: 'numeric', 
      month: 'long', 
      day: 'numeric'
    });
  };

  // Format amount for display
  const formatAmount = (amount) => {
    return amount.toLocaleString('fr-FR', { 
      style: 'currency', 
      currency: 'EUR' 
    });
  };

  // Render loading or error states
  if (loading) return <div className="loading">Chargement des dépenses...</div>;
  if (error) return <div className="error-message">Erreur: {error}</div>;

  return (
    <div className="expenses-page">
      {/* Notification */}
      <AnimatePresence>
        {notification.show && (
          <Notification
            message={notification.message}
            type={notification.type}
            onClose={() => setNotification({ show: false, message: '', type: 'success' })}
          />
        )}
      </AnimatePresence>

      {/* Confirmation Modal */}
      <ConfirmationModal
        isOpen={confirmationModal.isOpen}
        onClose={() => setConfirmationModal({ isOpen: false, itemToDelete: null })}
        onConfirm={handleDelete}
        title="Supprimer la dépense"
        message="Êtes-vous sûr de vouloir supprimer cette dépense ? Cette action est irréversible."
        confirmText="Supprimer"
        cancelText="Annuler"
      />

      {/* Page Header */}
      <div className="expenses-header">
        <h1>Gestion des dépenses</h1>
        <motion.button 
          className="add-button" 
          onClick={() => setShowForm(!showForm)}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          {showForm ? 'Annuler' : 'Ajouter une dépense'}
        </motion.button>
      </div>
      
      {/* Add/Edit Form */}
      <AnimatePresence>
        {showForm && (
          <motion.div 
            className="expense-form-container"
            initial={{ opacity: 0, height: 0, overflow: 'hidden' }}
            animate={{ opacity: 1, height: 'auto', overflow: 'visible' }}
            exit={{ opacity: 0, height: 0, overflow: 'hidden' }}
            transition={{ duration: 0.3, ease: "easeInOut" }}
          >
            <h2>{isEditing ? 'Modifier la dépense' : 'Nouvelle dépense'}</h2>
            <form onSubmit={handleSubmit} className="expense-form">
              <div className="form-group">
                <label htmlFor="name">Intitulé</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="category">Catégorie</label>
                <select
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  required
                >
                  <option value="">Sélectionner une catégorie</option>
                  <option value="Locaux">Locaux</option>
                  <option value="Charges">Charges</option>
                  <option value="Services">Services</option>
                  <option value="Matériel">Matériel</option>
                  <option value="Salaires">Salaires</option>
                  <option value="Marketing">Marketing</option>
                  <option value="Autre">Autre</option>
                </select>
              </div>
              
              <div className="form-group">
                <label htmlFor="amount">Montant (€)</label>
                <input
                  type="number"
                  id="amount"
                  name="amount"
                  value={formData.amount}
                  onChange={handleChange}
                  step="0.01"
                  min="0"
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="expense_date">Date</label>
                <input
                  type="date"
                  id="expense_date"
                  name="expense_date"
                  value={formData.expense_date}
                  onChange={handleChange}
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="notes">Notes (optionnel)</label>
                <textarea
                  id="notes"
                  name="notes"
                  value={formData.notes}
                  onChange={handleChange}
                  rows="3"
                />
              </div>
              
              <div className="form-group">
                <label>
                  <input
                    type="checkbox"
                    name="recurring"
                    checked={formData.recurring}
                    onChange={handleChange}
                  />
                  Dépense récurrente
                </label>
                {formData.recurring && (
                  <select
                    name="recurring_period"
                    value={formData.recurring_period}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Sélectionner la période</option>
                    <option value="monthly">Mensuel</option>
                    <option value="quarterly">Trimestriel</option>
                    <option value="yearly">Annuel</option>
                  </select>
                )}
              </div>
              
              <div className="form-buttons">
                <button 
                  type="button" 
                  onClick={resetForm} 
                  className="cancel-button"
                >
                  Annuler
                </button>
                <button 
                  type="submit" 
                  className="save-button"
                >
                  {isEditing ? 'Mettre à jour' : 'Enregistrer'}
                </button>
              </div>
            </form>
          </motion.div>
        )}
      </AnimatePresence>
      
      {/* Expense Statistics */}
      <div className="expenses-stats">
        {[
          { label: 'Total des dépenses', value: formatAmount(expenseStats.total) },
          { label: 'Nombre de dépenses', value: expenseStats.count },
          { label: 'Moyenne', value: formatAmount(expenseStats.average) }
        ].map((stat, index) => (
          <motion.div 
            key={stat.label}
            className="stat-card"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.1 + index * 0.1, duration: 0.3 }}
          >
            <h3>{stat.label}</h3>
            <p>{stat.value}</p>
          </motion.div>
        ))}
      </div>
      
      {/* Expenses Table */}
      <motion.div 
        className="expenses-table-container"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 0.5 }}
      >
        <table className="expenses-table">
          <thead>
            <tr>
              <th>Intitulé</th>
              <th>Catégorie</th>
              <th>Montant</th>
              <th>Date</th>
              <th>Notes</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {expenses.length === 0 ? (
              <tr>
                <td colSpan="6" className="no-data">Aucune dépense enregistrée</td>
              </tr>
            ) : (
              expenses.map((expense) => (
                <tr key={expense.id}>
                  <td>{expense.name}</td>
                  <td>{expense.category}</td>
                  
                  <td className="amount-cell">{formatAmount(expense.amount)}</td>
                  <td>{formatDate(expense.expense_date)}</td>
                  <td className="notes-cell">{expense.notes || 'N/A'}</td>
                  <td className="actions-cell">
                    <motion.button 
                      onClick={() => handleEdit(expense)} 
                      className="edit-button"
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      Modifier
                    </motion.button>
                    <motion.button 
                      onClick={() => handleDeleteConfirmation(expense.id)} 
                      className="delete-button"
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      Supprimer
                    </motion.button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </motion.div>
    </div>
  );
}

export default Expenses;
